<?phpnamespace BackBundle\Controller;use BackBundle\Entity\Commercial;use BackBundle\Form\CommercialType;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use Sensio\Bundle\FrameworkExtraBundle\Configuration\Route;use Symfony\Component\HttpFoundation\Request;use Symfony\Component\HttpFoundation\Response;use BackBundle\Entity\User;/** * Class UserController * @package BackBundle\Controller * @Route("/commercial") */class CommercialController extends Controller{    /**     * @Route("/all", name="back_commercial_list")     * @param Request $request     * @return Response     */    public function indexAction(Request $request)    {        $em = $this->getDoctrine()->getEntityManager();//      $limit = ($request->cookies->has("show_limit")) ? $request->cookies->get("show_limit") : 10;        $pageLimited = array(20, 30, 50, 100);        $limit = ($request->get('limit') !== null && in_array($request->get('limit'), $pageLimited)) ? $request->get('limit') : 20;        $search = ($request->get('q') != null) ? $request->get('q') : null;        $query = $em->getRepository('BackBundle:Commercial')->createQueryBuilder('commercial');        if ($search !== null) {            $query->where('commercial.lastName like :search')                ->orWhere('commercial.firstName like :search')                ->setParameter('search', "%" . $search . "%");        }        $query->orderBy('commercial.id', 'DESC');        $commercials = $this->get('knp_paginator')            ->paginate(                $query, /* query NOT result */                $request->query->getInt('page', 1)/*page number*/,                $limit/*limit per page*/            );        return $this->render('BackBundle:Commercial:list.html.twig', array(                'commercials' => $commercials,                'pageLimited' => $pageLimited,            )        );    }    /**     * @Route("/add", name="back_commercial_add")     * @param Request $request     * @return Response     */    public function addAction(Request $request)    {        $em = $this->getDoctrine()->getManager();        $commercial = new Commercial();        $form = $this->createForm(CommercialType::class, $commercial);        $form->handleRequest($request);        if ($form->isSubmitted() && $form->isValid()) {            $em->persist($commercial);            $em->flush();            if (null !== $commercial->getId()) {                $container = $this->container;                $userManager = $container->get('fos_user.user_manager');                if ($userManager->findUserByUsernameOrEmail($commercial->getEmail()) == null &&                    $userManager->findUserByUsernameOrEmail($commercial->getLogin()) == null                ) {                    $manipulator = $container->get('fos_user.util.user_manipulator');                    $user = $manipulator->create($commercial->getLogin(), $commercial->getPassword(), $commercial->getEmail(), $inactive = false, $superadmin = false);                    $user->setCommercial($commercial);                    $user->addRole(User::ROLE_COMMERCIAL);                    dump($user);                    die;                }                if (null !== $form->get('image_upload')->getData()) {                    $imgPath = $this->get('back_upload_service')->uploadProfile(                        $form->get('image_upload')->getData(),                        $commercial->getSlug(),                        "commercial"                    );                    if (null !== $imgPath) {                        $commercial->setImage($imgPath);                    }                }                $em->flush();                $this->setFlash("custom-alerts alert alert-success fade in",                    '<i class="fa fa-check"></i> Le commercial (<b>' . $commercial->getLastName() . " " . $commercial->getFirstName() . '</b>) a été bien créé.');                return $this->redirectToRoute('back_commercial_list');            } else {                $this->setFlash("custom-alerts alert alert-danger fade in",                    '<i class="fa fa-warning"></i> <b>‹Erreur </b> Une erreur est produite lors de la création de l\'objet commercial.');            }        } else if ($form->isSubmitted() && !$form->isValid()) {            $this->setFlash("custom-alerts alert alert-danger fade in", '<i class="fa fa-warning"></i> Une Erreur s\'est produite lors de la validation des champs.');        }        return $this->render('BackBundle:Commercial:add.html.twig', array(            'form' => $form->createView(),        ));    }    /**     * @Route("/edit/{slug}", name="back_commercial_edit")     * @param Request $request     * @param $slug     * @return Response     */    public function editAction(Request $request, $slug)    {        die("Edit");        return $this->render('BackBundle:Pages:index . html . twig');    }    /**     * @Route("/remove", name="back_commercial_remove")     * @param Request $request     * @return Response     */    public function removeAction(Request $request)    {        die("remove");        return $this->render('BackBundle:Pages:index . html . twig');    }    /**     * @Route("/data", name="back_commercial_data")     * @param Request $request     * @return Response     */    public function addedAction(Request $request)    {        die("ADD Commercial ====> Warning");        $em = $this->getDoctrine()->getEntityManager();        $commercials = [            ["lastName" => "Horn",                "firstName" => "Kaitlin ",                "email" => "kaitlinhorn@opportech.com",                "phone" => "07 13 54 37 96",                "image" => "https://unsplash.it/200/300?image=727"],            ["lastName" => "Banks",                "firstName" => "Chandra ",                "email" => "chandrabanks@opportech.com",                "phone" => "04 59 2 20 70", 