<?phpnamespace BackBundle\Controller;use BackBundle\Entity\customer;use BackBundle\Entity\Company;use BackBundle\Form\CustomerType;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use Sensio\Bundle\FrameworkExtraBundle\Configuration\Route;use Symfony\Component\HttpFoundation\Request;use Symfony\Component\HttpFoundation\Response;use BackBundle\Entity\User;/** * Class UserController * @package BackBundle\Controller * @Route("/customer") */class CustomerController extends Controller{    /**     * @Route("/all", name="back_customer_list")     * @param Request $request     * @return Response     */    public function indexAction(Request $request)    {        $em = $this->getDoctrine()->getEntityManager();//      $limit = ($request->cookies->has("show_limit")) ? $request->cookies->get("show_limit") : 10;//        $this->setFlash("custom-alerts alert alert-success fade in", '<i class="fa fa-check"></i> Chaine introuvable');        $pageLimited = array(20, 30, 50, 100);        $limit = ($request->get('limit') !== null && in_array($request->get('limit'), $pageLimited)) ? $request->get('limit') : 20;        $search = ($request->get('q') != null) ? $request->get('q') : null;        $query = $em->getRepository('BackBundle:Customer')            ->createQueryBuilder('customer')            ->leftJoin('customer.commercial', 'commercial')            ->leftJoin('customer.company', 'company');        if ($search !== null) {            $query->where('customer.lastName like :search')                ->orWhere('customer.firstName like :search')                ->setParameter('search', "%" . $search . "%")                ->set;        }        $query->orderBy('customer.id', 'DESC');        $customers = $this->get('knp_paginator')            ->paginate(                $query, /* query NOT result */                $request->query->getInt('page', 1)/*page number*/,                $limit/*limit per page*/            );        return $this->render('BackBundle:Customer:list.html.twig', array(                'customers' => $customers,                'pageLimited' => $pageLimited,            )        );    }    /**     * @Route("/add", name="back_customer_add")     * @param Request $request     * @return Response     */    public function addAction(Request $request)    {        $em = $this->getDoctrine()->getManager();        $customer = new Customer();        $form = $this->createForm(CustomerType::class, $customer);        $form->handleRequest($request);        if ($form->isSubmitted() && $form->isValid()) {            $em->persist($customer);            $em->flush();            if ($customer->getId() !== null) {                $container = $this->container;                $userManager = $container->get('fos_user.user_manager');                if ($userManager->findUserByUsernameOrEmail($customer->getEmail()) == null &&                    $userManager->findUserByUsernameOrEmail($customer->getLogin() == null)                ) {                    $manipulator = $container->get('fos_user.util.user_manipulator');                    $user = $manipulator->create($customer->getLogin(), $customer->getPassword(), $customer->getEmail(), $inactive = false, $superadmin = false);                    $user->setCustomer($customer);                    $user->addRole(User::ROLE_CUSTOMER);                }                if (null !== $form->get('image_upload')->getData()) {                    $imgPath = $this->get('back_upload_service')->uploadProfile(                        $form->get('image_upload')->getData(),                        $customer->getSlug(),                        "customer"                    );                    if (null !== $imgPath) {                        $customer->setImage($imgPath);                    }                }                $em->flush();                $this->setFlash("custom-alerts alert alert-success fade in",                    '<i class="fa fa-check"></i> Le client (<b>' . $customer->getLastName() . " " . $customer->getFirstName() . '</b>) a été bien créé.');                return $this->redirectToRoute('back_customer_list');            } else {                $this->setFlash("custom-alerts alert alert-danger fade in",                    '<i class="fa fa-warning"></i> <b>‹Erreur </b> Une erreur est survenue lors de la création de l\'objet client.');            }        } else if ($form->isSubmitted() && !$form->isValid()) {            $this->setFlash("custom-alerts alert alert-danger fade in", '<i class="fa fa-warning"></i> Erreur lors de la validation des champs.');        }        return $this->render('BackBundle:Customer:add.html.twig', array(            'form' => $form->createView(),        ));    }    /**     * @Route("/edit/{slug}", name="back_customer_edit")     * @param Request $request     * @param $slug     * @return Response     */    public    function editAction(Request $request, $slug)    {        die("Edit");        return $this->render('BackBundle:Pages:index.html.twig');    }    /**     * @Route("/remove", name="back_customer_remove",  options = {"expose"=true})     * @param Request $request     * @return Response     */    public    function removeAction(Request $request)    {        if ($request->isXmlHttpRequest()) {            $em = $this->getDoctrine()->getManager();            $id = $request->get('id');            if (!empty($id)) {                $object = $em->getRepository('BackBundle:Customer')->find($id);                $message = "Le client (<b>" . $object->getLastName() . " " . $object->getFirstName() . "</b>) a été supprimé.";                $em->remove($object);                $em->flush();                return new Response(json_encode(array(                        "result" => "success",                        "message" => $message)                ), 200, ['Content-Type' => 'application/json']);            }            $message = "Customer ID is empty ";        } else {            $message = "Error: isXmlHttpRequest";        }        return new response (json_encode(array('result' => 'error', "message" => $message)), 200, ['Content-Type' => 'application/json']);    }    /**     * @Route("/back_artist_visible_ajax", name="back_artist_visible_ajax", options = {"expose"=true})     * @param Request $request     * @return Response     */    function removeBackArtistAjaxAction(Request $request)    {        if ($request->isXmlHttpRequest()) {            $em = $this->getDoctrine()->getManager();            $id = $request->get('id');            if (!empty($id)) {                $object = $em->getRepository('BackBundle:Artist')->find($id);                $object->setVisible(false);                // $em->remove($object);                $em->flush();                return new Response(json_encode(array(                        "result" => "success",                        "message" => "L'Artiste a été supprimé")                ), 200, ['Content-Type' => 'application/json']);            }            $message = "Artist ID  is empty ";        } else {            $message = "Error: isXmlHttpRequest";        }        return new response (json_encode(array('result' => 'error', "message" => $message)), 200, ['Content-Type' => 'application/json']);    }    /*    * @doc custom-alerts alert alert-danger fade in    */    private    function setFlash($action, $value)    {        $this->container->get('session')->getFlashBag()->set($action, $value);    }    /**     * @Route("/data", name="back_customer_data")     * @param Request $request     * @return Response     */    public    function addedAction(Request $request)    {        die("ADD ====> Warning");        $em = $this->getDoctrine()->getEntityManager();        $customers = [            ["name" => "CANDECOR",                "lastName" => "Stephens",                "fir